mapper.select:
SELECT

from truck_admin_shop


    and shop_name like #{shopName}


    and shop_id = #{shopId}

    and shop_status=1

SELECT

from truck_admin_shop


    and shop_name like #{shopName}


    and shop_id = #{shopId}

SELECT

from truck_admin_shop


    and shop_name like #{shopName}


    and shop_id = #{shopId}


    and admin_id = #{adminId}

SELECT

from truck_admin_shop
where status = 1

  and shop_name like #{shopName}


  and category_id in

    #{item}

select

from truck_admin_shop
where admin_id in(
    select admin_id from truck_product
    where Product_ID in (
            select product_id from truck_order_detail where order_id=#{orderId}
    )
    group by admin_id
)
